import React, {Component, Fragment} from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import {Routes} from 'react-router-dom';
import './App.css';
import Navbar from './Components/layout/Navbar';
import UserItem from './Components/layout/users/UserItem';
import User from './Components/layout/users/User';
import Users from './Components/layout/users/Users';
import axios from 'axios';
import Search from './Components/layout/users/Search';
import Alert from './Components/layout/Alert';
import About from './Components/pages/About';
class App extends Component {
    state = {
        users: [],
        user: {},
        loading: false,
        alert: null
    }

    searchUsers = async text => {
        this.setState({loading: true})
        const res = await axios.get(`https://api.github.com/search/users?q=${text}&client_id=${
            process.env.REACT_APP_GITHUB_CLIENT_ID
        }&client_secret=${
            process.env.REACT_APP_GITHUB_CLIENT_SECRET
        }`);

        this.setState({users: res.data.items, loading: false});

    }

    getUser = async (username) => {
                this.setState({loading: true})
        const res = await axios.get(`https://api.github.com/users?${username}?client_id=${
            process.env.REACT_APP_GITHUB_CLIENT_ID
        }&client_secret=${
            process.env.REACT_APP_GITHUB_CLIENT_SECRET
        }`);

        this.setState({user: res.data, loading: false});

    }
    clearUsers = () => this.
        setState({ users: [], loading: false });
    setAlert = (msg, type) => {
        this.setState({
            alert: {
                msg,
                type
            }
        });
        setTimeout(() => this.setState({alert: null}), 2000)
    }
    render() {
        const {users, user, loading} = this.state;
        return (<Router>
            <div className='App'>
                <Navbar/>
                <div className='container'>
                    <Alert alert={
                        this.state.alert
                    }></Alert>

                    <Routes>

                        <Route exact path="/"
                            element={
                                (<Fragment>
                                    <Search searchUsers={
                                            this.searchUsers
                                        }
                                        clearUsers={
                                            this.clearUsers
                                        }
                                        showClear={
                                            users.length > 0 ? true : false
                                        }
                                        setAlert={
                                            this.setAlert
                                        }/>
                                    <Users loading={loading}
                                        users={users}/>

                                </Fragment>)
                            }/>
                        <Route exact path='/about'
                            element={<About />} />
                        <Route exact path='/user/:login' element={<User getUser={this.getUser} user={user} loading={loading} />}/>
                    </Routes>


                </div>
            </div>
        </Router>);
    }
}
export default App;
